define(
    [
        'jquery', 'underscore', 'oae.core'
    ],
    function ($, _, oae) {

        return function (uid, show, constants) {

            // Properties
            var $rootel = $('#' + uid);

            /**
             * When an authentication button gets clicked
             * @param   {Event}     e                   The dispatched event
             * @param   {Object}    e.currentTarget     The object that dispatched the event
             * @return  {Boolean}                       Prevent the form being submitted by default
             */
            var onButtonClickHandler = function(e) {
                var id = e.currentTarget.id;
                switch(id){
                    case 'btnLogin':
                        doLocalLogin();
                        break;
                    case 'btnFacebook':
                        doSocialLogin(constants.authentication.types.facebook);
                        break;
                    case 'btnGoogle':
                        doSocialLogin(constants.authentication.types.google);
                        break;
                    case 'btnTwitter':
                        doSocialLogin(constants.authentication.types.twitter);
                        break;
                }
                return false;
            };

            var doLocalLogin = function() {
                console.log('[MobileLogin] onButtonClickHandler => login with local');
                var username = $('#txtUsername').val();
                var password = $('#txtPassword').val();
                if(username && password){
                    $(document).trigger(
                        constants.authentication.events.loginattempt,
                        {
                            username: username,
                            password: password,
                            callback: function(err){
                                if(err){

                                    // TODO: Replace with custom modal view
                                    var message = "Wrong username and/or password";
                                    window.alert(message);

                                    // TODO: set message as event message object
                                    $(document).trigger(constants.alerts.init);
                                }
                            }
                        }
                    );
                }else{

                    // TODO: Replace with custom modal view

                    var message = oae.api.i18n.translate('__MSG__PLEASE_ENTER_YOUR_PASSWORD_AND_USERNAME__');
                    window.alert(message);

                    // TODO: set message as event message object
                    $(document).trigger(constants.alerts.init);
                }
            };

            var doSocialLogin = function(type) {
                $(document).trigger(
                    constants.authentication.events.socialloginattempt, type,
                    function(err){
                        if(err){
                            console.log('[LoginView] doSocialLogin callback');
                        }
                    }
                );
            };

            /**
             * Build widget children
             */
            var initChildren = function() {
                oae.api.util.template().render('#mobilelogin-widget-template', {}, $('#mobile-login-form-container', $rootel));
            };

            /**
             * Bind events to components
             */
            var addBinding = function() {
                $('button').bind('click', onButtonClickHandler);
            };

            /**
             * Initialize widget
             */
            var init = function() {
                // Init children
                initChildren();
                // Add binding
                addBinding();
            };

            init();
        };
    }
);