define(
    [
        'jquery', 'underscore', 'oae.core'
    ],
    function ($, _, oae) {

        return function (uid, show, data, callback) {

            // Properties
            var $rootel = $('#' + uid);
            var _data = data;

            /**
             * Build widget children
             */
            var initChildren = function() {
                _.each(_data.items, function(item){
                    var label = oae.api.i18n.translate(item.label);
                    $rootel.find('#oae-mobile-menu').append('<li class="item" data-action="' + item.action +'"><span>' + label + '</span></li>');
                });
            };

            /**
             * Initialize widget
             */
            var init = function () {
                initChildren();
            };

            init();
        };
    }
);